const Menu = () => {
  return (
    <main classNasme="menu">
      <h2>Our Menu</h2>
      <Pizza
        name="Pizza Spinaci"
        ingredients="Tomato, mozarella, spinach, and ricotta cheese"
        photoname="pizzas/spinaci.jpg"
      />
      <Pizza
        name="Mozerrla Pizza"
        ingredients="mozarella, spinach, and ricotta cheese"
        photoname="pizzas/funghi.jpg"
      />
    </main>
  );
};

function Pizza(props) {
  return (
    <div className="pizzaWrapper">
      <img src={props.photoname} alt="Pizza" />
      <h3>{props.name}</h3>
      <span>{props.ingredients}</span>
    </div>
  );
}

export default Menu;
